import React from "react";
import axios from 'axios';
import ConditionalInput from '../conditionalInput/ConditionalInput';
import DashboardComponent from '../dashboardComponent/DashboardComponent';




export class MainComponent extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            username:'',
            password:'',
            isLoginSuccess: false
        }
    }



    // componentDidMount(){
    //     axios.get('http://localhost:8080/regulation')
    //     .then((data) =>{
    //                 this.setState({regulations:data.data}) 
    //                 console.log(this.state.regulations)
    //                 })
    //     .catch(error => {                    
    //     });     
    // }

    // renderStateOverViewComponents = (regulations) => {   
    //     return(
    //         <div>
    //             {regulations.map((regulation) => {
    //                 return <OverviewStateComponent regData={regulation}/>
    //             })}

    //         </div>
    //     ) 
    // }


    handleLoginClick = () => {
        if(this.state.username === "admin" && this.state.password === "adminpasswort"){
            this.setState({
                isLoginSuccess: true
            })
        }    
    }

    updateUsernameInput = (event) => {
        this.setState({
            username: event.target.value
        })
    }

    updatePasswordInput = (event) => {
        this.setState({
            passoword: event.target.value
        })
    }

    render() {


        return(
            <div>
                {!this.state.isLoginSuccess? 
                <div className="loginWrapper">
                    <label className="loginLabel">LOGIN</label>
                    <ConditionalInput                               
                        inputValue={this.state.username}
                        labelText='Nutzername'
                        type='text'
                        returnValue={this.updateUsernameInput}
                    />
                    <ConditionalInput                               
                        inputValue={this.state.password}
                        labelText='Passwort'
                        type='text'
                        returnValue={this.updatePasswordInput}
                    />
                    <button onClick={this.handleLoginClick}> 
                        login
                    </button> 
                </div> 
                :
                <button >Logout</button>}

                {this.state.isLoginSuccess? <DashboardComponent /> : null}

            </div>
        )
    }
}



export default MainComponent;
